      "type": "Financial Action",
      "description": "The act of taking on more debt."
    },
    {
      "id": "Spread",
      "type": "Financial Metric",
      "description": "The difference between two prices or rates, typically for financial instruments."
    },
    {
      "id": "Xs Curve",
      "type": "Economic Model Component",
      "description": "A theoretical curve representing credit supply or demand within an economic model."
    },
    {
      "id": "Is Curve",
      "type": "Economic Model Component",
      "description": "The Investment-Saving curve, representing equilibrium in the goods market in macroeconomic models."
    },
    {
      "id": "Income",
      "type": "Economic Metric",
      "description": "The total earnings received by individuals or entities."
    },
    {
      "id": "Interest Rate",
      "type": "Financial Metric",
      "description": "The cost of borrowing money or the return on savings."
    },
    {
      "id": "Savers",
      "type": "Economic Agent",
      "description": "Individuals or entities who save money by deferring consumption."
    },
    {
      "id": "Output",
      "type": "Economic Metric",
      "description": "The total quantity of goods and services produced in an economy."
    },
    {
      "id": "Lm/Mp Curves",
      "type": "Economic Model Component",
      "description": "The Liquidity-Money (LM) or Monetary Policy (MP) curves, representing equilibrium in the money market or central bank policy."
    }
  ],
  "edges": [
    {
      "source": "Intermediation",
      "target": "Economic Shocks",
      "relation": "introduces_channel_for",
      "description": "The role of intermediation introduces a new channel for the amplification and propagation of economic shocks."
    },
    {
      "source": "Economic Activity Level",
      "target": "Asset Prices",
      "relation": "affects",
      "description": "A high level of economic activity affects asset prices."
    },
    {
      "source": "Asset Prices",
      "target": "Net Worth Of Financial Intermediaries",
      "relation": "impacts",
      "description": "Affected asset prices influence the net worth of financial intermediaries."
    },
    {
      "source": "Asset Prices",
      "target": "Borrowers' Net Worth",
      "relation": "impacts",
      "description": "Affected asset prices influence the net worth of borrowers."
    },
    {
      "source": "Net Worth Of Financial Intermediaries",
      "target": "Additional Borrowing",
      "relation": "enables",
      "description": "Increased net worth of financial intermediaries allows for additional borrowing."
    },
    {
      "source": "Borrowers' Net Worth",
      "target": "Additional Borrowing",
      "relation": "enables",
      "description": "Increased net worth of borrowers allows for additional borrowing."
    },
    {
      "source": "Additional Borrowing",
      "target": "Spread",
      "relation": "occurs_at_any_level_of",
      "description": "Additional borrowing occurs at any level of spread."
    },
    {
      "source": "Additional Borrowing",
      "target": "Xs Curve",
      "relation": "causes_shift_to_right_of",
      "description": "Additional borrowing causes a shift of the XS curve to the right."
    },
    {
      "source": "Additional Borrowing",
      "target": "Is Curve",
      "relation": "contributes_to_flattening_of",
      "description": "The allowance for additional borrowing, partly via the XS curve shift, makes the IS curve flatter."
    },
    {
      "source": "Is Curve",
      "target": "Income",
      "relation": "links_to",
      "description": "The IS curve establishes a relationship where changes in income are associated with changes in the interest rate."
    },
    {
      "source": "Is Curve",
      "target": "Interest Rate",
      "relation": "links_to",
      "description": "The IS curve establishes a relationship where changes in interest rate are associated with changes in income."
    },
    {
      "source": "Interest Rate",
      "target": "Savers",
      "relation": "paid_to",
      "description": "Interest rates are paid to savers."
    },
    {
      "source": "Intermediation",
      "target": "Output",
      "relation": "amplifies_effects_on",
      "description": "The overall effect of intermediation, particularly making the IS curve flatter, amplifies the effects on output from shifts in other curves."
    },
    {
      "source": "Lm/Mp Curves",
      "target": "Output",
      "relation": "have_effects_on",
      "description": "Shifts in the LM/MP curves have effects on output."
    }
  ]
}

